        beforeSubmit.push(function () {

                if(jq('#shock').find(':checkbox').prop('checked')) {
                    var shockAnswer = jq('#shock-answer').find("input[type='radio']:checked").val();
                    if(!shockAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'Shock' under Complication");
                        return false;
                    }
                }

                if(jq('#bacteria').find(':checkbox').prop('checked')) {
                    var bacteriaAnswer = jq('#bacteria-answer').find("input[type='radio']:checked").val();
                    if(!bacteriaAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'Bacteria' under Complication");
                        return false;
                    }
                }

                if(jq('#seizure').find(':checkbox').prop('checked')) {
                    var seizureAnswer = jq('#seizure-answer').find("input[type='radio']:checked").val();
                    if(!seizureAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'seizure' under Complication");
                        return false;
                    }
                }

                if(jq('#bleeding').find(':checkbox').prop('checked')) {
                    var bleedingAnswer = jq('#bleeding-answer').find("input[type='radio']:checked").val();
                    if(!bleedingAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'bleeding' under Complication");
                        return false;
                    }
                }

